import React, { PureComponent } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useParams,
} from "react-router-dom";
import { Component } from "react/cjs/react.production.min";
import LoginComponent from "./LoginComponent.jsx";

class TodoApp extends PureComponent {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <div className="TodoAapp">
        <Router>
          <Routes>
            <Route path="/" element={<LoginComponent />}></Route>
            <Route path="/login" element={<LoginComponent />}></Route>
            <Route path="/welcome/:name" element={<WelcomeComponent />}></Route>
            <Route path="/todos" element={<ListTodosComponent />}></Route>
            <Route element={<ErrorComponent />}></Route>
          </Routes>
        </Router>

        {/* <LoginComponent />
        <WelcomeComponent /> */}
      </div>
    );
  }
}

class ListTodosComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [
        { id: 1, description: "Learn React" },
        { id: 2, description: "Become an expert in React" },
        { id: 3, description: "Learn Spring Boot" },
      ],
    };
  }

  render() {
    return (
      <div>
        <h1>List Todos</h1>
        <table>
          <thead>
            <tr>
              <th>id</th>
              <th>description</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{this.state.todos.map.description}</td>
              <td>{this.state.todos.description}</td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

function WelcomeComponent() {
  let { name } = useParams();
  return <div>Hey {name}! Welcome to your Todo Application.</div>;
}

function ErrorComponent() {
  return <div>An Error Occured!!! Please reload the application. </div>;
}

