import React, { PureComponent } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useParams,
  Link,
} from "react-router-dom";
import { Component } from "react/cjs/react.production.min";
import LoginComponent from "./LoginComponent.jsx";

class TodoApp extends PureComponent {
  constructor(props) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <div className="TodoAapp">
        <Router>
          <Routes>
            <Route path="/" element={<LoginComponent />}></Route>
            <Route path="/login" element={<LoginComponent />}></Route>
            <Route path="/welcome/:name" element={<WelcomeComponent />}></Route>
            <Route path="/todos" element={<ListTodosComponent />}></Route>
            <Route element={<ErrorComponent />}></Route>
          </Routes>
        </Router>

        {/* <LoginComponent />
        <WelcomeComponent /> */}
      </div>
    );
  }
}

class ListTodosComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [
        {
          id: 1,
          description: "Learn React",
          done: false,
          targetDate: new Date(),
        },
        {
          id: 2,
          description: "Become an expert in React",
          done: false,
          targetDate: new Date(),
        },
        {
          id: 3,
          description: "Learn Spring Boot",
          done: false,
          targetDate: new Date(),
        },
      ],
    };
  }

  render() {
    return (
      <div>
        <h1>List Todos</h1>
        <table>
          <thead>
            <tr>
              <th>id</th>
              <th>description</th>
              <th>Target Date</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.todos.map((todo) => (
              <tr>
                <td>{todo.id}</td>
                <td>{todo.description}</td>
                <td>{todo.done.toString()}</td>
                <td>{todo.targetDate.toString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

function WelcomeComponent() {
  let { name } = useParams();
  return (
    <div>
      Hey {name}! You can manage your Todos <Link to="/todos">here.</Link>
    </div>
  );
}

function ErrorComponent() {
  return <div>An Error Occured!!! Please reload the application. </div>;
}

